    let image_data_url = this.canvas.toDataURL('image/jpeg');
    this.userProvidedPhotoSRC = image_data_url;
  }

  stopCamera() {
    this.stream.getTracks().forEach(function (track: any) {
      track.stop();
    });
  }


  openURL() {
    this.openDigilockerURL();
